contracts/PaymentStream.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {IPaymentStream} from "./interfaces/IPaymentStream.sol";
import {IPaymentStreamFactoryMetadata} from "./interfaces/IPaymentStreamFactoryMetadata.sol";

// solhint-disable custom-errors, immutable-vars-naming
contract PaymentStream is AccessControl, IPaymentStream {
    using SafeERC20 for IERC20;

    // solhint-disable var-name-mixedcase
    string public VERSION;
    // solhint-enable var-name-mixedcase
    string public constant NAME = "PaymentStream";

    address public immutable payer;
    address public immutable token;

    address public payee;
    address public fundingAddress;

    uint256 public usdAmount;
    uint256 public startTime;
    uint256 public secs;
    uint256 public usdPerSec;
    uint256 public claimed;

    bool public paused;

    IPaymentStreamFactoryMetadata public immutable factory;

    bytes32 private constant ADMIN_ROLE = keccak256(abi.encodePacked("admin"));
    bytes32 private constant PAUSABLE_ROLE = keccak256(abi.encodePacked("pausable"));

    modifier onlyPayer() {
        require(_msgSender() == payer, "not-stream-owner");
        _;
    }

    modifier onlyPayerOrDelegated() {
        require(_msgSender() == payer || hasRole(PAUSABLE_ROLE, _msgSender()), "not-stream-owner-or-delegated");
        _;
    }

    modifier onlyPayee() {
        require(_msgSender() == payee, "not-payee");
        _;
    }

    /**
     * @notice Creates a new payment stream
     * @dev Payer is set as admin of "PAUSABLE_ROLE", so he can grant and revoke the "pausable" role later on
     * @param _payer Owner of the stream
     * @param _payee address that receives the payment stream
     * @param _usdAmount uint256 total amount in USD (scaled to 18 decimals) to be distributed until endTime
     * @param _token address of the ERC20 token that payee receives as payment
     * @param _fundingAddress address used to withdraw the drip
     * @param _endTime timestamp that sets drip distribution end
     */
    constructor(
        address _payer,
        address _payee,
        uint256 _usdAmount,
        address _token,
        address _fundingAddress,
        uint256 _endTime
    ) {
        factory = IPaymentStreamFactoryMetadata(_msgSender());

        VERSION = factory.VERSION();

        require(_endTime > block.timestamp, "invalid-end-time");
        require(_payee != _fundingAddress, "payee-is-funding-address");
        require(_payee != address(0) && _fundingAddress != address(0), "payee-or-funding-address-is-0");

        require(_usdAmount > 0, "usd-amount-is-0");

        payee = _payee;
        usdAmount = _usdAmount;
        token = _token;
        fundingAddress = _fundingAddress;
        payer = _payer;
        startTime = block.timestamp;
        secs = _endTime - block.timestamp;
        usdPerSec = _usdAmount / secs;

        require(usdPerSec != 0, "usd-per-sec-is-0");

        _setupRole(ADMIN_ROLE, _payer);
        _setRoleAdmin(PAUSABLE_ROLE, ADMIN_ROLE);
    }

    /**
     * @notice Delegates pausable capability to new delegate
     * @dev Only RoleAdmin (Payer) can delegate this capability, tx will revert otherwise
     * @param _delegate address that receives the "PAUSABLE_ROLE"
     */
    function delegatePausable(address _delegate) external override {
        require(_delegate != address(0), "invalid-delegate");

        grantRole(PAUSABLE_ROLE, _delegate);
    }

    /**
     * @notice Revokes pausable capability of a delegate
     * @dev Only RoleAdmin (Payer) can revoke this capability, tx will revert otherwise
     * @param _delegate address that has its "PAUSABLE_ROLE" revoked
     */
    function revokePausable(address _delegate) external override {
        revokeRole(PAUSABLE_ROLE, _delegate);
    }

    /**
     * @notice Pauses a stream if caller is either the payer or a delegate of PAUSABLE_ROLE
     */
    function pauseStream() external override onlyPayerOrDelegated {
        paused = true;
        emit StreamPaused();
    }

    /**
     * @notice Unpauses a stream if caller is either the payer or a delegate of PAUSABLE_ROLE
     */
    function unpauseStream() external override onlyPayerOrDelegated {
        paused = false;
        emit StreamUnpaused();
    }

    /**
     * @notice If caller is the payer of the stream it sets a new address as receiver of the stream
     * @param _newPayee address of new payee
     */
    function updatePayee(address _newPayee) external override onlyPayer {
        require(_newPayee != address(0), "invalid-new-payee");
        require(_newPayee != payee, "same-new-payee");
        require(_newPayee != fundingAddress, "new-payee-is-funding-address");

        _claim();

        emit PayeeUpdated(payee, _newPayee);
        payee = _newPayee;
    }

    /**
     * @notice If caller is the payer of the stream it sets a new address used to withdraw the drip
     * @param _newFundingAddress new address used to withdraw the drip
     */
    function updateFundingAddress(address _newFundingAddress) external override onlyPayer {
        require(_newFundingAddress != address(0), "invalid-new-funding-address");
        require(_newFundingAddress != fundingAddress, "same-new-funding-address");
        require(_newFundingAddress != payee, "new-funding-address-is-payee");

        emit FundingAddressUpdated(fundingAddress, _newFundingAddress);

        fundingAddress = _newFundingAddress;
    }

    /**
     * @notice If caller is the payer it increases or decreases a stream funding rate
     * @dev Any unclaimed drip amount remaining will be claimed on behalf of payee
     * @param _usdAmount uint256 total amount in USD (scaled to 18 decimals) to be distributed until endTime
     * @param _endTime timestamp that sets drip distribution end
     */
    function updateFundingRate(uint256 _usdAmount, uint256 _endTime) external override onlyPayer {
        require(_endTime > block.timestamp, "invalid-end-time");

        _claim();

        usdAmount = _usdAmount;
        startTime = block.timestamp;
        secs = _endTime - block.timestamp;
        usdPerSec = _usdAmount / secs;
        claimed = 0;

        emit StreamUpdated(_usdAmount, _endTime);
    }

    /**
     * @notice If caller is the payee of the stream it receives the accrued drip amount
     */
    function claim() external override onlyPayee {
        require(!paused, "stream-is-paused");
        _claim();
    }

    function claimable() external view override returns (uint256) {
        return _claimable();
    }

    /**
     * @notice Helper function, gets the accrued drip of given stream converted into target token amount
     * @return uint256 amount in target token
     */
    function claimableToken() external view override returns (uint256) {
        return factory.usdToTokenAmount(token, _claimable());
    }

    function _claim() internal {
        uint256 _accumulated = _claimable();

        if (_accumulated == 0) return;

        uint256 _amount = factory.usdToTokenAmount(token, _accumulated);

        claimed += _accumulated;

        IERC20(token).safeTransferFrom(fundingAddress, payee, _amount);

        emit Claimed(_accumulated, _amount, token);
    }

    /**
     * @notice gets the accrued drip of given stream in USD
     * @return uint256 USD amount (scaled to 18 decimals)
     */
    function _claimable() internal view returns (uint256) {
        uint256 _elapsed = block.timestamp - startTime;

        if (_elapsed > secs) {
            return usdAmount - claimed; // no more drips to avoid floating point dust
        }

        return (usdPerSec * _elapsed) - claimed;
    }
}

contracts/PaymentStreamFactory.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {AggregatorV2V3Interface} from "@chainlink/contracts/src/v0.8/interfaces/AggregatorV2V3Interface.sol";
import {FeedRegistryInterface} from "@chainlink/contracts/src/v0.8/interfaces/FeedRegistryInterface.sol";
import {Denominations} from "@chainlink/contracts/src/v0.8/Denominations.sol";
import {IPaymentStreamFactory} from "./interfaces/IPaymentStreamFactory.sol";
import {PaymentStream} from "./PaymentStream.sol";

// solhint-disable custom-errors
contract PaymentStreamFactory is IPaymentStreamFactory, Ownable {
    string public constant VERSION = "1.0.2";
    string public constant NAME = "PaymentStreamFactory";

    address internal constant WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;

    address[] private allStreams;
    mapping(address => bool) private isOurs;

    // Chainlink Feed Registry: https://docs.chain.link/docs/feed-registry/
    // Aggregates all supported price feeds in one handy factory contract
    // Automatically supported TOKEN/USD and TOKEN/ETH pairs: https://docs.chain.link/docs/ethereum-addresses/
    FeedRegistryInterface public feedRegistry;

    // Some tokens like ETH or BTC have special addresses in the feed registry
    // token address => token denomination in Feed Registry
    mapping(address => address) public customFeedMapping;

    // Staleness tolerance to reject quote from ChainLink
    uint256 public stalenessTolerance = 24 hours;

    constructor(address _feedRegistry) {
        feedRegistry = FeedRegistryInterface(_feedRegistry);

        customFeedMapping[WETH] = Denominations.ETH;
    }

    /**
     * @notice Creates a new payment stream
     * @dev Payer (_msgSender()) is set as admin of "pausableRole", so he can grant and revoke the "pausable" role later on
     * @param _payee address that receives the payment stream
     * @param _usdAmount uint256 total amount in USD (scaled to 18 decimals) to be distributed until endTime
     * @param _token address of the ERC20 token that payee receives as payment
     * @param _fundingAddress address used to withdraw the drip
     * @param _endTime timestamp that sets drip distribution end
     */
    function createStream(
        address _payee,
        uint256 _usdAmount,
        address _token,
        address _fundingAddress,
        uint256 _endTime
    ) external returns (address streamAddress) {
        // Prevents the caller to create a Stream with an unsupported token
        // In case a USD/TOKEN or ETH/TOKEN Pair doesn't exist
        // This will revert with "Feed not found"
        usdToTokenAmount(_token, _usdAmount);

        streamAddress = address(new PaymentStream(_msgSender(), _payee, _usdAmount, _token, _fundingAddress, _endTime));

        allStreams.push(streamAddress);
        isOurs[streamAddress] = true;

        emit StreamCreated(allStreams.length - 1, streamAddress, _msgSender(), _payee, _usdAmount);
    }

    /**
     * @notice Updates Chainlink FeedRegistry contract address
     * @dev Only contract owner can change feedRegistry
     * @param _newAddress address of new Chainlink FeedRegistry instance
     */
    function updateFeedRegistry(address _newAddress) external override onlyOwner {
        require(_newAddress != address(0), "invalid-feed-registry-address");
        require(_newAddress != address(feedRegistry), "same-feed-registry-address");

        emit FeedRegistryUpdated(address(feedRegistry), _newAddress);
        feedRegistry = FeedRegistryInterface(_newAddress);
    }

    /**
     * @notice Defines a custom mapping for token denominations in the Feed Registry
     * @param _token address of the ERC20 token
     * @param _denomination the denomination address that the feed registry uses for _token
     */
    function updateCustomFeedMapping(address _token, address _denomination) external onlyOwner {
        require(_denomination != address(0), "invalid-custom-feed-map");
        require(_denomination != customFeedMapping[_token], "same-custom-feed-map");

        customFeedMapping[_token] = _denomination;
        emit CustomFeedMappingUpdated(_token, _denomination);
    }

    /**
     * @notice Defines a staleness tolerance for ChainLink price quote
     * It can be set to 0 to recover the drip
     * @param _newTolerance new tolerance range in seconds
     */
    function updateStalenessTolerance(uint256 _newTolerance) external onlyOwner {
        emit StalenessToleranceUpdated(stalenessTolerance, _newTolerance);
        stalenessTolerance = _newTolerance;
    }

    /**
     * @notice Converts given amount in usd to target token amount using oracle
     * @param _token address of target token
     * @param _amount amount in USD (scaled to 18 decimals)
     * @return lastPrice target token amount
     */
    function usdToTokenAmount(address _token, uint256 _amount) public view override returns (uint256 lastPrice) {
        // tries a direct _token -> USD pair first
        try feedRegistry.getFeed(_tokenDenomination(_token), Denominations.USD) returns (AggregatorV2V3Interface) {
            uint256 _quote = _getQuote(_token, Denominations.USD);
            lastPrice = ((_amount * 1e18) / _quote) / 10 ** (18 - IERC20Metadata(_token).decimals());
        } catch {
            // If getFeed reverts, uses token/ETH/usd route
            // If a feed doesn't exist for _token/ETH, it will revert with "Feed not found"
            uint256 _ethQuote = _getQuote(_token, Denominations.ETH);
            uint256 _ethUsdQuote = _getQuote(Denominations.ETH, Denominations.USD);
            uint256 _amountInETH = (_amount * 1e18) / _ethUsdQuote;
            lastPrice = ((_amountInETH * 1e18) / _ethQuote) / 10 ** (18 - IERC20Metadata(_token).decimals());
        }
    }

    /**
     * @notice Checks if a address belongs to this contract' streams
     */
    function ours(address _a) external view override returns (bool) {
        return isOurs[_a];
    }

    /**
     * @notice Returns no. of streams stored in contract
     */
    function getStreamsCount() external view override returns (uint256) {
        return allStreams.length;
    }

    /**
     * @notice Returns address of the stream located at given id
     */
    function getStream(uint256 _idx) external view override returns (address) {
        return allStreams[_idx];
    }

    function _getQuote(address _base, address _quote) internal view returns (uint256) {
        (, int256 _price, , uint256 _updatedAt, ) = feedRegistry.latestRoundData(_tokenDenomination(_base), _quote);

        // If stalenessTolerance is set ( > 0)
        // Reject quote from the oracle if it's outside time range
        require((stalenessTolerance == 0 || (block.timestamp - _updatedAt <= stalenessTolerance)), "stale-oracle");

        // USD decimals is 8 in ChainLink, scales it up to 18 decimals
        _price = (_quote == Denominations.USD) ? _price * 1e10 : _price;
        return uint256(_price);
    }

    function _tokenDenomination(address _token) internal view returns (address) {
        return (customFeedMapping[_token] == address(0)) ? _token : customFeedMapping[_token];
    }
}

